
public class H5a {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int W=10,G=20,K=30, A=40, T;
	    System.out.println("Before swapping: W="+W+", G="+G+", K="+K+", A="+A);
	    T=W;
	    W=G;
	    G=K;
	    K=A;
	    A=T;
	    System.out.println("After swapping: W="+W+", G="+G+", K="+K+", A="+A);
	}

}
